import React, { useEffect, useState } from "react";

function Restaurantes() {
  const [restaurante, setRestaurante] = useState([]);
  const [hora, setHora] = useState([]);
  useEffect(() => {
    obtenerDatos()
obtenerDia()
  }, []);
  const obtenerDatos = async () => {
    const data = await fetch(
      "http://hoteloasis.vicentealdhair.com/api/oasis/centro_consumo"
    );
    const restaurantes = await data.json();
    const categoria = restaurantes.filter(categorias=>categorias.categoria_id==2)
    setRestaurante(categoria);
  };
  const obtenerDia= async()=>{
    const horarios = await fetch(
      "http://hoteloasis.vicentealdhair.com/api/oasis/centro_consumo/horario"
    );
    var fecha=new Date();
    var dia= fecha.getDay()
    console.log(dia)
    const fechas = await horarios.json();
    console.log(fechas);
  }



  return (
    <div className="container__componente">
      <div className="container__restaurantes">
        <div className="scroll">
          <h2 className="titulo__restaurante">Restaurantes</h2>
        </div>
        <div className="container__contenido">
          {!restaurante
            ? "  Cargando..."
            : restaurante.map(filteredres => (
                <div className="contenido__api">
                  <h3 className="titulo__indivi dual">{filteredres.nombre}</h3>
                  <h4 className="concepto">{filteredres.concepto_es}</h4>
                </div>
              ))}
             
        </div>
      </div>
    </div>
  );
}

export default Restaurantes;
