import React, { useEffect, useState } from "react";

function Bar() {
  const [bar, setBar] = useState([]);
  useEffect(() => {
    obtenerBar();
  }, []);
  const obtenerBar = async () => {
    const data = await fetch(
      "http://192.168.1.128:8000/api/oasis/centro_consumo/bares"
    );
    const bares = await data.json();


    setBar(bares);
  };
  

  
  const cerrado= bar.filter(rescat=>rescat.mensaje);
const abierto= bar.filter(rescat=>rescat.categoria);
  return (
    <div className="container__componente">
      <div className="container__bar">
        <div className="scroll">
          <h2 className="titulo__restaurante">Bares</h2>
        </div>
        <div className="container__contenido">
        {!seismedia ? "  Cargando..." : ce.map((item) => (<h1>No hay bares abiertos</h1>))}
        {abierto.map((filteredres) => (
            <div className="contenido__api">
              <h3 className="titulo__individual">
              {filteredres.nombre}
              </h3>
              <h4 className="concepto">{filteredres.concepto_es}</h4>
              <h3 className="titulo__individual">
              ABIERTO HOY
              </h3>
              <h4 className="concepto">{filteredres.horario_inicio}-{filteredres.horario_final}</h4>
         
           
           <button>VER M√ÅS</button>
            </div>
          ))}

            {cerrado.map((filteredres) => (
            <div className="contenido__api">
              <p className="titulo__individual">
              <h1>{filteredres.mensaje}</h1>
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Bar;
