import React, { useEffect, useState } from "react";

function Bar() {
  const [bar, setBar] = useState([]);
  useEffect(() => {
    obtenerBar();
  }, []);
  const obtenerBar = async () => {
    const data = await fetch(
      "http://192.168.1.128:8000/api/oasis/centro_consumo/bares"
    );
    const bares = await data.json();
    const categoriabar=bares.filter(barescat=>barescat.categoria==3||barescat.dia);

    setBar(categoriabar);
  };
  const seismedia=bar.filter(bares=>bares.dia)
  
  return (
    <div className="container__componente">
      <div className="container__bar">
        <div className="scroll">
          <h2 className="titulo__restaurante">Restaurantes</h2>
        </div>
        <div className="container__contenido">
        {!seismedia ? "  Cargando..." : bar.map((item) => <h1>No hay bares abiertos</h1>)}
        {!seismedia ? "  Cargando..." : bar.map((item) => <h1>No hay bares abiertos</h1>)}
        </div>
      </div>
    </div>
  );
}

export default Bar;
