import React, { useEffect, useState } from "react";

function Bar() {
  const [bar, setBar] = useState([]);
  useEffect(() => {
    obtenerBar();
  }, []);
  const obtenerBar = async () => {
    const data = await fetch(
      "http://hoteloasis.vicentealdhair.com/api/oasis/centro_consumo"
    );
    const bares = await data.json();
    const categoriabar=bares.filter(barescat=>barescat.categoria_id==3)
    setBar(categoriabar);
  };
  return (
    <div className="container__componente">
      <div className="container__bar">
        <div className="scroll">
          <h2 className="titulo__restaurante">Restaurantes</h2>
        </div>
        <div className="container__contenido">
        {!bar ? "  Cargando..." : bar.map((item) => <li>{item.nombre}</li>)}
        </div>
      </div>
    </div>
  );
}

export default Bar;
